import React, {useState} from 'react';

function Slides({ slides }) {
    var [curr, setCurr] = useState(slides[0])

    var length = slides.length;
    function gotoNext() {

        let i = slides.indexOf(curr)
        if (i === (length-1)) 
            setCurr = slides[0]
            else
            setCurr = slides[i+1]

    }
function gotoPrev(){
     let i = slides.indexOf(curr)
        if (i === 0) 
            curr = slides[slides.length]
            else
            curr = slides[i-1]
       }
 function   refresh(){
      curr = slides[0]
    }
    if (!Array.isArray(slides) || length <= 0) {
        return null;
    }
   return (
        <div>
            <div id="navigation" className="text-center">
                <button data-testid="button-restart" className="small outlined" onClick={refresh}>Restart</button>
                <button data-testid="button-prev" className="small"  onClick={gotoPrev}   >Prev</button>
                <button data-testid="button-next" className="small"   onClick={gotoNext}>Next</button>
            </div>
            <div id="slide" className="card text-center">
               <h1 data-testid="title">{slides.title}</h1>
               <p data-testid="text">{slides.text}</p>
            </div>
        </div>
    );

}

export default Slides;
